contracts/KYCVerification.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title KYCVerification
 * @notice KYC/AML Verification contract for managing user verification status
 * @dev Integrates with Bridge API for KYC/AML screening
 * This contract manages:
 * - KYC verification requests
 * - KYC approval/rejection
 * - AML screening status
 * - Verification history
 * - Risk scoring
 */
contract KYCVerification {
    // Verification status enum
    enum VerificationStatus {
        PENDING,
        APPROVED,
        REJECTED,
        EXPIRED,
        SUSPENDED
    }

    // User verification record
    struct VerificationRecord {
        address userAddress;
        string bridgeReferenceId;
        VerificationStatus status;
        uint256 submissionTimestamp;
        uint256 approvalTimestamp;
        uint256 expiryTimestamp;
        string amlStatus;
        uint8 riskScore;
        string verificationData;
        bool documentVerified;
        bool faceVerified;
        string rejectionReason;
    }

    // KYC provider configuration
    struct KYCProvider {
        address providerAddress;
        string providerName;
        bool isActive;
        uint256 approvalCount;
        uint256 rejectionCount;
    }

    // Mapping of user address to verification record
    mapping(address => VerificationRecord) public verifications;

    // Mapping of reference ID to user address
    mapping(string => address) public referenceIdToUser;

    // Mapping of KYC providers
    mapping(address => KYCProvider) public kycProviders;

    // Array of all KYC provider addresses
    address[] public providersList;

    // Admin address
    address public admin;

    // Contract owner
    address public contractOwner;

    // Default verification expiry period (365 days)
    uint256 public verificationExpiryPeriod;

    // Maximum risk score allowed (0-100)
    uint8 public maxAllowedRiskScore;

    // Events
    event VerificationSubmitted(
        address indexed user,
        string referenceId,
        uint256 timestamp
    );

    event VerificationApproved(
        address indexed user,
        string referenceId,
        uint256 expiryTimestamp,
        uint8 riskScore
    );

    event VerificationRejected(
        address indexed user,
        string referenceId,
        string reason
    );

    event VerificationExpired(address indexed user, string referenceId);

    event VerificationSuspended(
        address indexed user,
        string referenceId,
        string reason
    );

    event KYCProviderAdded(address indexed provider, string providerName);

    event KYCProviderRemoved(address indexed provider);

    event KYCProviderActivated(address indexed provider);

    event KYCProviderDeactivated(address indexed provider);

    event RiskScoreUpdated(
        address indexed user,
        uint8 oldScore,
        uint8 newScore
    );

    event AMLStatusUpdated(address indexed user, string amlStatus);

    event VerificationExpiryPeriodUpdated(uint256 newPeriod);

    event MaxRiskScoreUpdated(uint8 newMaxScore);

    /**
     * @dev Constructor initializes the KYC verification contract
     */
    constructor() {
        admin = msg.sender;
        contractOwner = msg.sender;
        verificationExpiryPeriod = 365 days;
        maxAllowedRiskScore = 50; // Default max risk score
    }

    /**
     * @dev Modifier to check if caller is admin
     */
    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can call this function");
        _;
    }

    /**
     * @dev Modifier to check if caller is authorized KYC provider
     */
    modifier onlyKYCProvider() {
        require(
            kycProviders[msg.sender].isActive,
            "Only active KYC providers can call this function"
        );
        _;
    }

    /**
     * @dev Adds a new KYC provider
     * @param provider Address of the KYC provider
     * @param providerName Name of the KYC provider
     */
    function addKYCProvider(address provider, string memory providerName) external onlyAdmin {
        require(provider != address(0), "Invalid provider address");
        require(bytes(providerName).length > 0, "Provider name required");
        require(!kycProviders[provider].isActive, "Provider already exists");

        kycProviders[provider] = KYCProvider({
            providerAddress: provider,
            providerName: providerName,
            isActive: true,
            approvalCount: 0,
            rejectionCount: 0
        });

        providersList.push(provider);
        emit KYCProviderAdded(provider, providerName);
    }

    /**
     * @dev Removes a KYC provider
     * @param provider Address of the KYC provider to remove
     */
    function removeKYCProvider(address provider) external onlyAdmin {
        require(kycProviders[provider].isActive, "Provider does not exist");
        kycProviders[provider].isActive = false;
        emit KYCProviderRemoved(provider);
    }

    /**
     * @dev Activates a KYC provider
     * @param provider Address of the KYC provider
     */
    function activateKYCProvider(address provider) external onlyAdmin {
        require(kycProviders[provider].providerAddress != address(0), "Provider does not exist");
        kycProviders[provider].isActive = true;
        emit KYCProviderActivated(provider);
    }

    /**
     * @dev Deactivates a KYC provider
     * @param provider Address of the KYC provider
     */
    function deactivateKYCProvider(address provider) external onlyAdmin {
        require(kycProviders[provider].isActive, "Provider is not active");
        kycProviders[provider].isActive = false;
        emit KYCProviderDeactivated(provider);
    }

    /**
     * @dev Submits a KYC verification request
     * @param user User address to verify
     * @param referenceId Bridge API reference ID
     * @param verificationData Encrypted verification data
     */
    function submitVerification(
        address user,
        string memory referenceId,
        string memory verificationData
    ) external onlyKYCProvider returns (bool) {
        require(user != address(0), "Invalid user address");
        require(bytes(referenceId).length > 0, "Reference ID required");
        require(
            verifications[user].submissionTimestamp == 0 ||
                verifications[user].status == VerificationStatus.REJECTED,
            "User already has pending verification"
        );

        verifications[user] = VerificationRecord({
            userAddress: user,
            bridgeReferenceId: referenceId,
            status: VerificationStatus.PENDING,
            submissionTimestamp: block.timestamp,
            approvalTimestamp: 0,
            expiryTimestamp: 0,
            amlStatus: "PENDING",
            riskScore: 0,
            verificationData: verificationData,
            documentVerified: false,
            faceVerified: false,
            rejectionReason: ""
        });

        referenceIdToUser[referenceId] = user;

        emit VerificationSubmitted(user, referenceId, block.timestamp);
        return true;
    }

    /**
     * @dev Approves KYC verification
     * @param user User address to approve
     * @param referenceId Bridge API reference ID
     * @param riskScore Risk score from Bridge API (0-100)
     * @param amlStatus AML screening status
     */
    function approveVerification(
        address user,
        string memory referenceId,
        uint8 riskScore,
        string memory amlStatus
    ) external onlyKYCProvider returns (bool) {
        require(user != address(0), "Invalid user address");
        require(
            verifications[user].status == VerificationStatus.PENDING,
            "Verification is not pending"
        );
        require(
            keccak256(abi.encodePacked(verifications[user].bridgeReferenceId)) ==
                keccak256(abi.encodePacked(referenceId)),
            "Reference ID mismatch"
        );
        require(riskScore <= maxAllowedRiskScore, "Risk score exceeds maximum allowed");

        uint256 expiryTime = block.timestamp + verificationExpiryPeriod;

        verifications[user].status = VerificationStatus.APPROVED;
        verifications[user].approvalTimestamp = block.timestamp;
        verifications[user].expiryTimestamp = expiryTime;
        verifications[user].riskScore = riskScore;
        verifications[user].amlStatus = amlStatus;
        verifications[user].documentVerified = true;
        verifications[user].faceVerified = true;

        kycProviders[msg.sender].approvalCount++;

        emit VerificationApproved(user, referenceId, expiryTime, riskScore);
        emit AMLStatusUpdated(user, amlStatus);

        return true;
    }

    /**
     * @dev Rejects KYC verification
     * @param user User address to reject
     * @param referenceId Bridge API reference ID
     * @param reason Rejection reason
     */
    function rejectVerification(
        address user,
        string memory referenceId,
        string memory reason
    ) external onlyKYCProvider returns (bool) {
        require(user != address(0), "Invalid user address");
        require(
            verifications[user].status == VerificationStatus.PENDING,
            "Verification is not pending"
        );
        require(bytes(reason).length > 0, "Rejection reason required");

        verifications[user].status = VerificationStatus.REJECTED;
        verifications[user].rejectionReason = reason;

        kycProviders[msg.sender].rejectionCount++;

        emit VerificationRejected(user, referenceId, reason);

        return true;
    }

    /**
     * @dev Suspends a user's KYC verification (e.g., for AML violations)
     * @param user User address to suspend
     * @param reason Suspension reason
     */
    function suspendVerification(address user, string memory reason)
        external
        onlyKYCProvider
        returns (bool)
    {
        require(user != address(0), "Invalid user address");
        require(
            verifications[user].status == VerificationStatus.APPROVED,
            "Only approved verifications can be suspended"
        );
        require(bytes(reason).length > 0, "Suspension reason required");

        verifications[user].status = VerificationStatus.SUSPENDED;
        verifications[user].amlStatus = "SUSPICIOUS";

        emit VerificationSuspended(user, verifications[user].bridgeReferenceId, reason);
        return true;
    }

    /**
     * @dev Updates risk score for a user
     * @param user User address
     * @param newRiskScore New risk score
     */
    function updateRiskScore(address user, uint8 newRiskScore)
        external
        onlyKYCProvider
        returns (bool)
    {
        require(user != address(0), "Invalid user address");
        require(
            verifications[user].status == VerificationStatus.APPROVED,
            "Verification is not approved"
        );
        require(newRiskScore <= maxAllowedRiskScore, "Risk score exceeds maximum allowed");

        uint8 oldScore = verifications[user].riskScore;
        verifications[user].riskScore = newRiskScore;

        emit RiskScoreUpdated(user, oldScore, newRiskScore);
        return true;
    }

    /**
     * @dev Gets verification status for a user
     * @param user User address
     */
    function getVerificationStatus(address user)
        external
        view
        returns (
            VerificationStatus status,
            uint256 expiryTime,
            uint8 riskScore,
            string memory amlStatus
        )
    {
        VerificationRecord memory record = verifications[user];

        // Check if verification has expired
        VerificationStatus currentStatus = record.status;
        if (
            currentStatus == VerificationStatus.APPROVED &&
            block.timestamp > record.expiryTimestamp
        ) {
            currentStatus = VerificationStatus.EXPIRED;
        }

        return (currentStatus, record.expiryTimestamp, record.riskScore, record.amlStatus);
    }

    /**
     * @dev Gets full verification record for a user
     * @param user User address
     */
    function getVerificationRecord(address user)
        external
        view
        returns (VerificationRecord memory)
    {
        return verifications[user];
    }

    /**
     * @dev Checks if user is KYC verified and not expired
     * @param user User address
     */
    function isVerifiedAndActive(address user) external view returns (bool) {
        VerificationRecord memory record = verifications[user];

        if (record.status != VerificationStatus.APPROVED) {
            return false;
        }

        if (block.timestamp > record.expiryTimestamp) {
            return false;
        }

        return true;
    }

    /**
     * @dev Gets KYC provider information
     * @param provider Provider address
     */
    function getProviderInfo(address provider)
        external
        view
        returns (KYCProvider memory)
    {
        return kycProviders[provider];
    }

    /**
     * @dev Gets all KYC providers
     */
    function getAllProviders() external view returns (address[] memory) {
        return providersList;
    }

    /**
     * @dev Sets verification expiry period
     * @param newPeriod New expiry period in seconds
     */
    function setVerificationExpiryPeriod(uint256 newPeriod) external onlyAdmin {
        require(newPeriod > 0, "Expiry period must be greater than zero");
        verificationExpiryPeriod = newPeriod;
        emit VerificationExpiryPeriodUpdated(newPeriod);
    }

    /**
     * @dev Sets maximum allowed risk score
     * @param newMaxScore New maximum risk score (0-100)
     */
    function setMaxAllowedRiskScore(uint8 newMaxScore) external onlyAdmin {
        require(newMaxScore <= 100, "Risk score cannot exceed 100");
        maxAllowedRiskScore = newMaxScore;
        emit MaxRiskScoreUpdated(newMaxScore);
    }

    /**
     * @dev Updates admin address
     * @param newAdmin New admin address
     */
    function setAdmin(address newAdmin) external onlyAdmin {
        require(newAdmin != address(0), "Invalid admin address");
        admin = newAdmin;
    }
}

contracts/MockPYUSD.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/// @title MockPYUSD - Mock ERC-20 token for testing
/// @notice Simple ERC-20 implementation for testing PYUSD functionality
/// @dev This is a test contract, not for production use

contract MockPYUSD {
    string public name = "Mock PayPal USD";
    string public symbol = "mPYUSD";
    uint8 public decimals = 6;
    uint256 public totalSupply;

    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    constructor() {
        totalSupply = 0;
    }

    function mint(address to, uint256 amount) external {
        require(to != address(0), "Cannot mint to zero address");
        totalSupply += amount;
        balanceOf[to] += amount;
        emit Transfer(address(0), to, amount);
    }

    function transfer(address to, uint256 value) external returns (bool) {
        require(balanceOf[msg.sender] >= value, "Insufficient balance");
        balanceOf[msg.sender] -= value;
        balanceOf[to] += value;
        emit Transfer(msg.sender, to, value);
        return true;
    }

    function transferFrom(address from, address to, uint256 value) external returns (bool) {
        require(balanceOf[from] >= value, "Insufficient balance");
        require(allowance[from][msg.sender] >= value, "Insufficient allowance");

        balanceOf[from] -= value;
        balanceOf[to] += value;
        allowance[from][msg.sender] -= value;

        emit Transfer(from, to, value);
        return true;
    }

    function approve(address spender, uint256 value) external returns (bool) {
        allowance[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }
}

contracts/Payments.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title Payments
 * @dev This contract manages PYUSD payments between users.
 * It requires users to approve this contract to spend their PYUSD tokens.
 */
contract Payments is Ownable {
    IERC20 public pyusdToken;

    event PaymentSent(address indexed from, address indexed to, uint256 amount);

    /**
     * @dev Sets the PYUSD token address.
     * @param _pyusdTokenAddress The address of the PYUSD ERC20 token contract.
     */
    constructor(address _pyusdTokenAddress) Ownable(msg.sender) {
        pyusdToken = IERC20(_pyusdTokenAddress);
    }

    /**
     * @dev Allows a user to send PYUSD to another user.
     * The sender must have approved this contract to spend at least the `amount` of PYUSD.
     * @param _to The recipient's address.
     * @param _amount The amount of PYUSD to send.
     */
    function sendPayment(address _to, uint256 _amount) external {
        require(_to != address(0), "Payments: cannot send to the zero address");
        require(_amount > 0, "Payments: amount must be greater than zero");

        // The contract needs to be approved by the sender to spend the amount
        uint256 allowance = pyusdToken.allowance(msg.sender, address(this));
        require(allowance >= _amount, "Payments: check token allowance");

        // Transfer the tokens from the sender to the recipient
        bool success = pyusdToken.transferFrom(msg.sender, _to, _amount);
        require(success, "Payments: token transfer failed");

        emit PaymentSent(msg.sender, _to, _amount);
    }

    /**
     * @dev Allows the owner to update the PYUSD token address.
     * @param _pyusdTokenAddress The new address of the PYUSD token contract.
     */
    function setTokenAddress(address _pyusdTokenAddress) external onlyOwner {
        pyusdToken = IERC20(_pyusdTokenAddress);
    }
}

contracts/PYUSDKYCSubscription.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/// @title PYUSDKYCSubscription - KYC/SBT-gated fee contract for PayPal USD (PYUSD) payments
/// @notice Accepts PYUSD payments only from KYC-verified users (SBT holders)
/// @dev Integrates with official PYUSD ERC-20 proxy and custom SBT for access control
/// @author Your Name
/// @custom:security-contact security@yourproject.com

interface IERC20 {
    function transferFrom(address from, address to, uint256 value) external returns (bool);
    function transfer(address to, uint256 value) external returns (bool);
    function balanceOf(address owner) external view returns (uint256);
    function decimals() external view returns (uint8);
}

interface ISBT {
    function balanceOf(address owner) external view returns (uint256);
}

contract PYUSDKYCSubscription {
    address public owner;
    IERC20 public pyusd;
    ISBT public sbt;
    uint256 public feeAmount; // PYUSD fee amount (in wei)

    // Track payment and reimbursement status
    mapping(address => bool) public paid;
    mapping(address => bool) public reimbursed;

    // Statistics
    uint256 public totalPaid;
    uint256 public totalReimbursed;

    /// @notice Emitted when a user pays the subscription fee
    event FeePaid(address indexed payer, uint256 amount, uint256 timestamp);

    /// @notice Emitted when a user claims ETH gas reimbursement
    event GasReimbursed(address indexed user, uint256 ethAmount, uint256 timestamp);

    /// @notice Emitted when owner deposits ETH for reimbursements
    event ETHDeposited(address indexed depositor, uint256 amount);

    /// @notice Emitted when owner withdraws PYUSD fees
    event FeesWithdrawn(address indexed owner, uint256 amount);

    /// @notice Emitted when fee amount is updated
    event FeeAmountUpdated(uint256 oldAmount, uint256 newAmount);

    /// @notice Emitted when ownership is transferred
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /// @dev Constructor sets PYUSD ERC-20 and SBT contract addresses
    /// @param _pyusd Official PYUSD ERC-20 proxy address (0x6c3ea9036406852006290770bedfcaba0e23a0e8 on mainnet)
    /// @param _sbt SoulBound Token contract address for KYC verification
    /// @param _feeAmount Fee amount in PYUSD (use 1e6 for 1 PYUSD if 6 decimals)
    constructor(address _pyusd, address _sbt, uint256 _feeAmount) {
        require(_pyusd != address(0), "Invalid PYUSD address");
        require(_sbt != address(0), "Invalid SBT address");
        require(_feeAmount > 0, "Fee must be positive");

        owner = msg.sender;
        pyusd = IERC20(_pyusd);
        sbt = ISBT(_sbt);
        feeAmount = _feeAmount;
    }

    /// @notice Modifier to ensure only KYC-verified users (SBT holders) can access
    modifier onlyKYC() {
        require(sbt.balanceOf(msg.sender) > 0, "Not KYC-verified (no SBT)");
        _;
    }

    /// @notice Modifier to ensure only contract owner can access
    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }

    /// @notice Pay the subscription/usage fee in PYUSD; only for verified SBT holders
    /// @dev User must first approve this contract to spend their PYUSD
    /// @dev Requires user to hold SBT (KYC verification)
    function paySubscription() external onlyKYC {
        require(!paid[msg.sender], "Already paid");
        require(pyusd.transferFrom(msg.sender, address(this), feeAmount), "PYUSD transfer failed");

        paid[msg.sender] = true;
        totalPaid += feeAmount;

        emit FeePaid(msg.sender, feeAmount, block.timestamp);
    }

    /// @notice Claim ETH as a gas reimbursement after successful payment
    /// @dev This helps cover gas costs for testnets/demos
    /// @param ethAmount Amount of ETH to claim (in wei)
    function claimEthGas(uint256 ethAmount) external onlyKYC {
        require(paid[msg.sender], "PYUSD payment required first");
        require(address(this).balance >= ethAmount, "Contract low on ETH");
        require(!reimbursed[msg.sender], "Already reimbursed");
        require(ethAmount > 0, "Amount must be positive");

        reimbursed[msg.sender] = true;
        totalReimbursed += ethAmount;

        payable(msg.sender).transfer(ethAmount);
        emit GasReimbursed(msg.sender, ethAmount, block.timestamp);
    }

    /// @notice Admin function to deposit ETH for reimbursement pool
    /// @dev Owner can fund the contract with ETH for gas reimbursements
    function depositETH() external payable onlyOwner {
        require(msg.value > 0, "Must send ETH");
        emit ETHDeposited(msg.sender, msg.value);
    }

    /// @notice Owner can withdraw collected PYUSD fees
    /// @dev Transfers all PYUSD balance to owner
    function withdrawFees() external onlyOwner {
        uint256 balance = pyusd.balanceOf(address(this));
        require(balance > 0, "No fees to withdraw");

        require(pyusd.transfer(owner, balance), "PYUSD transfer failed");
        emit FeesWithdrawn(owner, balance);
    }

    /// @notice Update the subscription fee amount
    /// @param _newFeeAmount New fee amount in PYUSD
    function updateFeeAmount(uint256 _newFeeAmount) external onlyOwner {
        require(_newFeeAmount > 0, "Fee must be positive");
        uint256 oldAmount = feeAmount;
        feeAmount = _newFeeAmount;
        emit FeeAmountUpdated(oldAmount, _newFeeAmount);
    }

    /// @notice Transfer ownership of the contract
    /// @param newOwner Address of the new owner
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "New owner cannot be zero address");
        require(newOwner != owner, "New owner must be different");

        address previousOwner = owner;
        owner = newOwner;
        emit OwnershipTransferred(previousOwner, newOwner);
    }

    /// @notice Check if a user has paid the subscription fee
    /// @param user Address to check
    /// @return True if user has paid
    function hasUserPaid(address user) external view returns (bool) {
        return paid[user];
    }

    /// @notice Check if a user has claimed ETH reimbursement
    /// @param user Address to check
    /// @return True if user has been reimbursed
    function hasUserBeenReimbursed(address user) external view returns (bool) {
        return reimbursed[user];
    }

    /// @notice Get contract statistics
    /// @return _totalPaid Total PYUSD collected
    /// @return _totalReimbursed Total ETH reimbursed
    /// @return _ethBalance Current ETH balance
    /// @return _pyusdBalance Current PYUSD balance
    function getStats() external view returns (
        uint256 _totalPaid,
        uint256 _totalReimbursed,
        uint256 _ethBalance,
        uint256 _pyusdBalance
    ) {
        return (
            totalPaid,
            totalReimbursed,
            address(this).balance,
            pyusd.balanceOf(address(this))
        );
    }

    /// @notice Emergency function to withdraw all ETH (owner only)
    /// @dev Use with caution - this drains the reimbursement pool
    function emergencyWithdrawETH() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No ETH to withdraw");
        payable(owner).transfer(balance);
    }

    /// @notice Receive ETH (for deposits)
    receive() external payable {
        emit ETHDeposited(msg.sender, msg.value);
    }
}

contracts/PYUSDPaymentGateway.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./SoulBoundToken.sol";

/**
 * @title PYUSDPaymentGateway
 * @notice Payment gateway with KYC compliance gating using SBT verification
 * @dev Integrates with SoulBoundToken for access control
 */
contract PYUSDPaymentGateway is Ownable, ReentrancyGuard {
    // PYUSD token contract
    IERC20 public pyusdToken;

    // SBT contract for KYC verification
    SoulBoundToken public sbtContract;

    // Payment structure
    struct Payment {
        address from;
        address to;
        uint256 amount;
        uint256 timestamp;
        string description;
        bool completed;
        string paymentId;
    }

    // Mapping from payment ID to payment details
    mapping(string => Payment) public payments;

    // Mapping from user to payment history
    mapping(address => string[]) public userPayments;

    // Fee structure
    uint256 public transactionFee = 25; // 0.25% in basis points
    uint256 public constant FEE_DENOMINATOR = 10000;

    // Maximum payment limits
    uint256 public dailyLimit = 10000 * 10**6; // $10,000 in PYUSD (6 decimals)
    uint256 public monthlyLimit = 100000 * 10**6; // $100,000 in PYUSD

    // User daily/monthly spending tracking
    mapping(address => mapping(uint256 => uint256)) public dailySpending;
    mapping(address => mapping(uint256 => uint256)) public monthlySpending;

    // Events
    event PaymentInitiated(
        string indexed paymentId,
        address indexed from,
        address indexed to,
        uint256 amount,
        string description
    );

    event PaymentCompleted(
        string indexed paymentId,
        address indexed from,
        address indexed to,
        uint256 amount,
        uint256 fee
    );

    event PaymentFailed(
        string indexed paymentId,
        address indexed from,
        string reason
    );

    event LimitsUpdated(
        uint256 newDailyLimit,
        uint256 newMonthlyLimit
    );

    event FeeUpdated(uint256 newFee);

    modifier onlyVerifiedUser() {
        require(sbtContract.isKYCVerified(msg.sender), "KYC verification required");
        _;
    }

    modifier validPayment(string memory paymentId) {
        require(bytes(paymentId).length > 0, "Invalid payment ID");
        require(payments[paymentId].from == address(0), "Payment ID already exists");
        _;
    }

    constructor(
        address _pyusdToken,
        address _sbtContract
    ) Ownable(msg.sender) {
        pyusdToken = IERC20(_pyusdToken);
        sbtContract = SoulBoundToken(_sbtContract);
    }

    /**
     * @notice Initiate a payment
     * @param to Recipient address
     * @param amount Payment amount in PYUSD
     * @param description Payment description
     * @param paymentId Unique payment identifier
     */
    function initiatePayment(
        address to,
        uint256 amount,
        string memory description,
        string memory paymentId
    ) external onlyVerifiedUser validPayment(paymentId) nonReentrant {
        require(to != address(0), "Invalid recipient address");
        require(amount > 0, "Amount must be greater than 0");
        require(to != msg.sender, "Cannot pay yourself");

        // Check daily limit
        uint256 today = block.timestamp / 1 days;
        uint256 userDailySpending = dailySpending[msg.sender][today];
        require(userDailySpending + amount <= dailyLimit, "Daily limit exceeded");

        // Check monthly limit
        uint256 thisMonth = block.timestamp / (30 days);
        uint256 userMonthlySpending = monthlySpending[msg.sender][thisMonth];
        require(userMonthlySpending + amount <= monthlyLimit, "Monthly limit exceeded");

        // Calculate fee
        uint256 fee = (amount * transactionFee) / FEE_DENOMINATOR;
        uint256 totalAmount = amount + fee;

        // Check balance
        require(pyusdToken.balanceOf(msg.sender) >= totalAmount, "Insufficient balance");

        // Create payment record
        payments[paymentId] = Payment({
            from: msg.sender,
            to: to,
            amount: amount,
            timestamp: block.timestamp,
            description: description,
            completed: false,
            paymentId: paymentId
        });

        // Add to user payment history
        userPayments[msg.sender].push(paymentId);

        // Update spending tracking
        dailySpending[msg.sender][today] += amount;
        monthlySpending[msg.sender][thisMonth] += amount;

        emit PaymentInitiated(paymentId, msg.sender, to, amount, description);
    }

    /**
     * @notice Complete a payment
     * @param paymentId Payment identifier
     */
    function completePayment(string memory paymentId) external nonReentrant {
        Payment storage payment = payments[paymentId];
        require(payment.from != address(0), "Payment not found");
        require(!payment.completed, "Payment already completed");
        require(payment.from == msg.sender, "Only payer can complete payment");

        // Calculate fee
        uint256 fee = (payment.amount * transactionFee) / FEE_DENOMINATOR;
        uint256 totalAmount = payment.amount + fee;

        // Check balance again
        require(pyusdToken.balanceOf(msg.sender) >= totalAmount, "Insufficient balance");

        // Transfer PYUSD
        require(pyusdToken.transferFrom(msg.sender, payment.to, payment.amount), "Transfer failed");
        require(pyusdToken.transferFrom(msg.sender, owner(), fee), "Fee transfer failed");

        // Mark as completed
        payment.completed = true;

        emit PaymentCompleted(paymentId, payment.from, payment.to, payment.amount, fee);
    }

    /**
     * @notice Cancel a payment
     * @param paymentId Payment identifier
     */
    function cancelPayment(string memory paymentId) external {
        Payment storage payment = payments[paymentId];
        require(payment.from != address(0), "Payment not found");
        require(!payment.completed, "Payment already completed");
        require(payment.from == msg.sender, "Only payer can cancel payment");

        // Mark as failed
        payment.completed = false;

        emit PaymentFailed(paymentId, payment.from, "Payment cancelled by user");
    }

    /**
     * @notice Get payment details
     * @param paymentId Payment identifier
     */
    function getPayment(string memory paymentId) external view returns (Payment memory) {
        return payments[paymentId];
    }

    /**
     * @notice Get user payment history
     * @param user User address
     */
    function getUserPayments(address user) external view returns (string[] memory) {
        return userPayments[user];
    }

    /**
     * @notice Get user spending for a specific day
     * @param user User address
     * @param day Day timestamp
     */
    function getDailySpending(address user, uint256 day) external view returns (uint256) {
        return dailySpending[user][day];
    }

    /**
     * @notice Get user spending for a specific month
     * @param user User address
     * @param month Month timestamp
     */
    function getMonthlySpending(address user, uint256 month) external view returns (uint256) {
        return monthlySpending[user][month];
    }

    /**
     * @notice Check if user can make payment
     * @param user User address
     * @param amount Payment amount
     */
    function canMakePayment(address user, uint256 amount) external view returns (bool, string memory) {
        // Check KYC verification
        if (!sbtContract.isKYCVerified(user)) {
            return (false, "KYC verification required");
        }

        // Check daily limit
        uint256 today = block.timestamp / 1 days;
        uint256 userDailySpending = dailySpending[user][today];
        if (userDailySpending + amount > dailyLimit) {
            return (false, "Daily limit exceeded");
        }

        // Check monthly limit
        uint256 thisMonth = block.timestamp / (30 days);
        uint256 userMonthlySpending = monthlySpending[user][thisMonth];
        if (userMonthlySpending + amount > monthlyLimit) {
            return (false, "Monthly limit exceeded");
        }

        // Check balance
        uint256 fee = (amount * transactionFee) / FEE_DENOMINATOR;
        uint256 totalAmount = amount + fee;
        if (pyusdToken.balanceOf(user) < totalAmount) {
            return (false, "Insufficient balance");
        }

        return (true, "Payment allowed");
    }

    /**
     * @notice Update payment limits
     * @param newDailyLimit New daily limit
     * @param newMonthlyLimit New monthly limit
     */
    function updateLimits(uint256 newDailyLimit, uint256 newMonthlyLimit) external onlyOwner {
        require(newDailyLimit > 0, "Daily limit must be greater than 0");
        require(newMonthlyLimit > 0, "Monthly limit must be greater than 0");
        require(newMonthlyLimit >= newDailyLimit, "Monthly limit must be >= daily limit");

        dailyLimit = newDailyLimit;
        monthlyLimit = newMonthlyLimit;

        emit LimitsUpdated(newDailyLimit, newMonthlyLimit);
    }

    /**
     * @notice Update transaction fee
     * @param newFee New fee in basis points
     */
    function updateFee(uint256 newFee) external onlyOwner {
        require(newFee <= 1000, "Fee cannot exceed 10%"); // Max 10%

        transactionFee = newFee;
        emit FeeUpdated(newFee);
    }

    /**
     * @notice Emergency function to update SBT contract
     * @param newSBTContract New SBT contract address
     */
    function updateSBTContract(address newSBTContract) external onlyOwner {
        require(newSBTContract != address(0), "Invalid SBT contract address");
        sbtContract = SoulBoundToken(newSBTContract);
    }

    /**
     * @notice Emergency function to update PYUSD token
     * @param newPYUSDToken New PYUSD token address
     */
    function updatePYUSDToken(address newPYUSDToken) external onlyOwner {
        require(newPYUSDToken != address(0), "Invalid PYUSD token address");
        pyusdToken = IERC20(newPYUSDToken);
    }
}

contracts/SoulBoundToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/// @title SoulBoundToken - EIP-5192 compliant Soul Bound Token for KYC verification
/// @notice Non-transferable NFT representing KYC verification status
/// @dev Implements EIP-5192 for soul bound tokens (non-transferable NFTs)
/// @author Your Name
/// @custom:security-contact security@yourproject.com

interface IERC5192 {
    /// @notice Emitted when the locking status is changed to locked.
    /// @dev If a token is minted and the status is locked, this event should be emitted.
    /// @param tokenId The identifier for a token.
    event Locked(uint256 indexed tokenId);

    /// @notice Emitted when the locking status is changed to unlocked.
    /// @dev If a token is minted and the status is unlocked, this event should be emitted.
    /// @param tokenId The identifier for a token.
    event Unlocked(uint256 indexed tokenId);

    /// @notice Returns the locking status of an Soulbound Token
    /// @dev SBTs are non-transferable, so they are always locked.
    /// @param tokenId The identifier for a Token.
    /// @return locked Whether the token is locked (always true for SBTs).
    function locked(uint256 tokenId) external view returns (bool locked);
}

contract SoulBoundToken is IERC5192 {
    string public name = "KYC Soul Bound Token";
    string public symbol = "KYC-SBT";
    address public owner;

    // Track SBT holders (address => tokenId)
    mapping(address => uint256) public tokenOf;
    mapping(uint256 => address) public ownerOf;
    mapping(address => bool) public hasSBT;

    uint256 public totalSupply;
    uint256 public nextTokenId = 1;

    /// @notice Emitted when a new SBT is minted
    event Minted(address indexed to, uint256 indexed tokenId);

    /// @notice Emitted when an SBT is revoked
    event Revoked(address indexed from, uint256 indexed tokenId);

    /// @notice Emitted when ownership is transferred
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor() {
        owner = msg.sender;
    }

    /// @notice Modifier to ensure only contract owner can access
    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }

    /// @notice Mint a new SBT to a user (after off-chain KYC verification)
    /// @dev Only owner can mint, and each address can only have one SBT
    /// @param to Address to mint the SBT to
    function mint(address to) external onlyOwner {
        require(to != address(0), "Cannot mint to zero address");
        require(!hasSBT[to], "Address already has SBT");

        uint256 tokenId = nextTokenId++;
        tokenOf[to] = tokenId;
        ownerOf[tokenId] = to;
        hasSBT[to] = true;
        totalSupply++;

        emit Minted(to, tokenId);
        emit Locked(tokenId); // SBTs are always locked (non-transferable)
    }

    /// @notice Revoke an SBT (for compliance violations)
    /// @dev Only owner can revoke SBTs
    /// @param from Address to revoke SBT from
    function revoke(address from) external onlyOwner {
        require(hasSBT[from], "Address does not have SBT");

        uint256 tokenId = tokenOf[from];
        delete tokenOf[from];
        delete ownerOf[tokenId];
        hasSBT[from] = false;
        totalSupply--;

        emit Revoked(from, tokenId);
        emit Unlocked(tokenId);
    }

    /// @notice Check if an address holds an SBT
    /// @param user Address to check
    /// @return True if user has SBT
    function balanceOf(address user) external view returns (uint256) {
        return hasSBT[user] ? 1 : 0;
    }

    /// @notice Get the token ID for a user
    /// @param user Address to check
    /// @return Token ID (0 if no SBT)
    function tokenIdOf(address user) external view returns (uint256) {
        return tokenOf[user];
    }

    /// @notice Check if a token is locked (always true for SBTs)
    /// @param tokenId Token ID to check
    /// @return Always true (SBTs are non-transferable)
    function locked(uint256 tokenId) external pure override returns (bool) {
        return true; // SBTs are always locked
    }

    /// @notice Check if a user is KYC verified
    /// @param user Address to check
    /// @return True if user is KYC verified (has SBT)
    function isKYCVerified(address user) external view returns (bool) {
        return hasSBT[user];
    }

    /// @notice Get all SBT holders (for admin purposes)
    /// @dev This function can be expensive for large numbers of holders
    /// @return Array of addresses that hold SBTs
    function getAllHolders() external view returns (address[] memory) {
        address[] memory holders = new address[](totalSupply);
        uint256 index = 0;

        // Note: This is a simplified implementation
        // In production, you might want to maintain a separate array
        // or use events to track holders more efficiently
        return holders;
    }

    /// @notice Transfer ownership of the contract
    /// @param newOwner Address of the new owner
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "New owner cannot be zero address");
        require(newOwner != owner, "New owner must be different");

        address previousOwner = owner;
        owner = newOwner;
        emit OwnershipTransferred(previousOwner, newOwner);
    }

    /// @notice Batch mint SBTs to multiple addresses
    /// @dev Only owner can batch mint
    /// @param recipients Array of addresses to mint SBTs to
    function batchMint(address[] calldata recipients) external onlyOwner {
        require(recipients.length > 0, "No recipients provided");
        require(recipients.length <= 100, "Too many recipients"); // Gas limit protection

        for (uint256 i = 0; i < recipients.length; i++) {
            address to = recipients[i];
            if (to != address(0) && !hasSBT[to]) {
                uint256 tokenId = nextTokenId++;
                tokenOf[to] = tokenId;
                ownerOf[tokenId] = to;
                hasSBT[to] = true;
                totalSupply++;

                emit Minted(to, tokenId);
                emit Locked(tokenId);
            }
        }
    }

    /// @notice Batch revoke SBTs from multiple addresses
    /// @dev Only owner can batch revoke
    /// @param addresses Array of addresses to revoke SBTs from
    function batchRevoke(address[] calldata addresses) external onlyOwner {
        require(addresses.length > 0, "No addresses provided");
        require(addresses.length <= 100, "Too many addresses"); // Gas limit protection

        for (uint256 i = 0; i < addresses.length; i++) {
            address from = addresses[i];
            if (hasSBT[from]) {
                uint256 tokenId = tokenOf[from];
                delete tokenOf[from];
                delete ownerOf[tokenId];
                hasSBT[from] = false;
                totalSupply--;

                emit Revoked(from, tokenId);
                emit Unlocked(tokenId);
            }
        }
    }

    /// @notice Get contract statistics
    /// @return _totalSupply Total number of SBTs minted
    /// @return _nextTokenId Next token ID to be minted
    function getStats() external view returns (uint256 _totalSupply, uint256 _nextTokenId) {
        return (totalSupply, nextTokenId);
    }

    /// @notice Prevent transfers (SBTs are non-transferable)
    /// @dev This function always reverts to enforce non-transferability
    function transfer(address, uint256) external pure {
        revert("SBTs are non-transferable");
    }

    /// @notice Prevent transfers (SBTs are non-transferable)
    /// @dev This function always reverts to enforce non-transferability
    function transferFrom(address, address, uint256) external pure {
        revert("SBTs are non-transferable");
    }

    /// @notice Prevent approvals (SBTs are non-transferable)
    /// @dev This function always reverts to enforce non-transferability
    function approve(address, uint256) external pure {
        revert("SBTs are non-transferable");
    }

    /// @notice Prevent approvals (SBTs are non-transferable)
    /// @dev This function always reverts to enforce non-transferability
    function setApprovalForAll(address, bool) external pure {
        revert("SBTs are non-transferable");
    }
}

